表单
&lt;form action = &quot;a.php&quot; method = &quot;get&quot;&gt;
  &lt;input type = &quot;test&quot; name = &quot;username&quot;/&gt;
  &lt;input type = &quot;password&quot; name = &quot;pwd&quot;/&gt;
  &lt;input type = &quot;submit&quot; name = &quot;submit&quot; value = &quot;OK&quot;/&gt;
&lt;/form&gt;

