  echo &quot;&lt;form action = &#39;a.php&#39; method = &#39;get&#39;&gt;&quot;;
  echo &quot;&lt;input type = &#39;test&#39; name = &#39;username&#39;/&gt;&quot;;
  echo &quot;&lt;input type = &#39;password&#39; name = &#39;pwd&#39;/&gt;&quot;;
